            new SpecificationDefinition {Description = "",Key = "printcolor", Name="Kleur", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "Het aantal inktkleuren inclusief de zwarte inkt.",Key = "inktkleuren", Name="Aantal inktkleuren", Type="number",Unit="number", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "Veel nieuwe printers beschikken tegenwoordig over meerdere inktcartridges. Dit betekent dat als een kleur op is, je niet de hele kleurencartridge hoeft te vervangen. Dit bespaart geld omdat je niet onnodig ongebruikte inkt hoeft weg te gooien.",Key = "separate_cartridges", Name="Separate inktcartridges", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "Hier wordt de maximale resolutie van de printer aangegeven. Omdat de printer op verschillende papiersoorten en diverse media kan afdrukken zal de resolutie variëren. De resolutie van een printer wordt aangegeven in dpi (dots per inch)  en geeft het aantal beeldpuntjes aan dat weergegeven wordt per inch. Hoe meer beeldpunten, hoe hoger de beeldkwaliteit van je afdruk (scherpere tekens en vloeiender overgangen).",Key = "printresbw", Name="Resolutie zwart/wit", Type="text",Unit="text", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "Hier wordt de maximale resolutie van de printer aangegeven. Omdat de printer op verschillende papiersoorten en diverse media kan afdrukken zal de resolutie variëren. De resolutie van een printer wordt aangegeven in dpi (dots per inch)  en geeft het aantal beeldpuntjes aan dat weergegeven wordt per inch. Hoe meer beeldpunten, hoe hoger de beeldkwaliteit van je afdruk (scherpere tekens en vloeiender overgangen).",Key = "printrescolor", Name="Resolutie kleur", Type="text",Unit="text", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "De printsnelheid die hier genoemd wordt, is de snelheid voor een standaardprint op A4- formaat(20x30). Dit is een gemiddelde snelheid, de printsnelheid is ook afhankelijk van je computer, de software die je gebruikt en het document dat je wilt gaan printen.",Key = "printspeedbw", Name="Printsnelheid zwart/wit", Type="number",Unit="number", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "De printsnelheid die hier genoemd wordt, is de snelheid voor een standaardprint op A4- formaat(20x30). Dit is een gemiddelde snelheid, de printsnelheid is ook afhankelijk van je computer, de software die je gebruikt en het document dat je wilt gaan printen.",Key = "printspeedcolor", Name="Printsnelheid kleur", Type="number",Unit="number", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "Sommige printers hebben een ingebouwde processor. Het voordeel hiervan is dat je sneller kunt printen en je computer minder wordt belast bij het printen. De snelheid van de processor wordt aangegeven in Megahertz (MHz). Hoe hoger het getal hoe sneller de processor is.",Key = "processor", Name="Eigen processor", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "Sommige printers hebben een ingebouwde processor. Het voordeel hiervan is dat je sneller kunt printen en je computer minder wordt belast bij het printen. De snelheid van de processor wordt aangegeven in Megahertz (MHz). Hoe hoger het getal hoe sneller de processor is.",Key = "processormhz", Name="Processorsnelheid", Type="number",Unit="number", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "",Key = "standalone", Name="Printen/bewerken zonder PC", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=14},
