            new SpecificationDefinition {Description = "Bij een printer met een geheugenkaartlezer kun je geheugenkaartjes uit een digitale camera of mobiele telefoon direct in de printer stoppen om foto's te bekijken.",Key = "cardreader", Name="Geheugenkaartlezer", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "Hier zie je welke geheugenkaarten de printer kan uitlezen.",Key = "media", Name="Ondersteunde geheugenkaarten", Type="dialog",Unit="dialog", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "",Key = "fotosoftware", Name="Fotosoftware", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "Deze techniek maakt het mogelijk om heel eenvoudig cd's of dvd's te bedrukken. Je stopt het schijfje gewoon in de printer. Let op: dit is alleen mogelijk met speciaal hiervoor bestemde cd- of dvd-schijfjes.",Key = "cdprint", Name="CD/DVD printen", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "Een aantal printers kan zelfs tot het formaat A3+ printen.",Key = "papera3", Name="Geschikt voor A3-papier", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "Hier zie je of de printer papier ook dubbelzijdig kan printen.",Key = "dubbelzijdig", Name="Dubbelzijdig printen", Type="dialog",Unit="dialog", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "Bij een aantal inkjetprinters is het mogelijk om je foto’s te printen zonder witte rand. Deze mogelijkheid is vaak alleen aanwezig als je gebruik maakt van speciaal fotopapier.",Key = "randloos", Name="Randloos printen", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "De papiercapaciteit geeft aan hoeveel vel A4-papier je gelijktijdig in de printer kunt stoppen.",Key = "papercapacity", Name="Papiercapaciteit", Type="number",Unit="number", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "",Key = "paperinput", Name="Papierinvoer", Type="dialog",Unit="dialog", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "Er zijn verschillende manieren om de printer met een PC te verbinden. De belangrijkste zijn usb of (via het netwerk) ethernet of wifi.",Key = "aansluitingen", Name="Aansluitingen", Type="dialog",Unit="dialog", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "Hier wordt aangegeven over hoeveel geheugen de printer standaard beschikt. De hoeveelheid geheugen is vooral belangrijk voor printers die in een netwerk worden opgenomen (waarbij meerdere pc-gebruikers op een printer printen). Meer geheugen zorgt ervoor dat de printer de opdrachten dan sneller en efficiënter verwerkt.",Key = "memory_n", Name="Geheugen", Type="number",Unit="number", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "Als je de printer veel gebruikt en hij staat dicht bij je in de buurt, dan is het geluidsniveau van de printer belangrijk. Veel printers maken geluid tijdens het printen. Het geluid van een printer wordt uitgedrukt in decibel (dB). Een printer met 35 dB wordt over het algemeen als prettig ervaren.",Key = "sound", Name="Geluidsniveau", Type="number",Unit="number", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "De meeste printers sluit je met een stekker aan op het stopcontact, maar er zijn ook al printers op de markt met een oplaadbare accu. Dit geeft je de mogelijkheid om de printer in te zetten op plaatsen waar geen stopcontact is.",Key = "stroomvoorziening", Name="Stroomvoorziening", Type="dialog",Unit="dialog", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "Energiezuinige printers zijn printers die aan bepaalde eisen voldoen en daardoor beter voor ons milieu zijn. Tegenwoordig springen vrijwel alle printers zuinig om met energie.",Key = "energysaving", Name="Energiezuinig", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "",Key = "verbruik", Name="Energieverbruik", Type="number",Unit="number", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "Hier kun je zien of de printer geschikt is voor het door jou gebruikte besturingssysteem. Bijna alle printers zijn tegenwoordig geschikt voor Windows XP, Vista en 7 en soms ook voor Mac OS X en Linux.",Key = "os", Name="Besturingsysteem", Type="dialog",Unit="dialog", Value="", ProductGroupID=14},
            new SpecificationDefinition {Description = "",Key = "pr_inkj_toevoegingbesturingssysteem", Name="Toevoeging besturingssysteem", Type="text",Unit="text", Value="", ProductGroupID=14},
