            new SpecificationDefinition {Description = "Het formaat van een lcd-monitor wordt uitgedrukt in inches en gemeten van linksonder naar rechtsboven in beeld. Eén inch is 2,54 centimeter.",Key = "displaysize", Name="Formaat (inch)", Type="number",Unit="number", Value="", ProductGroupID=15},
            new SpecificationDefinition {Description = "",Key = "formaatcm", Name="Formaat (cm)", Type="number",Unit="number", Value="", ProductGroupID=15},
            new SpecificationDefinition {Description = "",Key = "lm_widescreen", Name="Breedbeeld", Type="dialog",Unit="dialog", Value="", ProductGroupID=15},
            new SpecificationDefinition {Description = "",Key = "lm_breedbeeldformaat", Name="Beeldverhouding", Type="dialog",Unit="dialog", Value="", ProductGroupID=15},
            new SpecificationDefinition {Description = "De aanblik van een lcd-monitor wordt voor een groot deel bepaald door het matte of glanzende oppervlak van het beeldscherm. Een mat scherm heeft minder last van spiegeling, terwijl een glanzend scherm vaak een sprankelende kleurweergave heeft. Dit geldt overigens alleen voor het scherm zelf en niet voor de rand er omheen.",Key = "lm_matglanzend", Name="Mat/glanzend", Type="dialog",Unit="dialog", Value="", ProductGroupID=15},
            new SpecificationDefinition {Description = "",Key = "schermtype", Name="Schermtype", Type="dialog",Unit="dialog", Value="", ProductGroupID=15},
            new SpecificationDefinition {Description = "De gebruikte technologie voor een beeldscherm bepaalt voor een groot deel de kwaliteit van een lcd-monitor. Elke schermtechniek heeft zijn specifieke sterke en minder sterke kanten. Er zijn vier technologieën: TN, MVA, PVA en IPS.",Key = "lm_schermtechniek", Name="Schermtechniek", Type="dialog",Unit="dialog", Value="", ProductGroupID=15},
            new SpecificationDefinition {Description = "3D is een extra effect bij je beeldscherm waarbij het lijkt alsof de beelden uit je scherm komen. Hierbij heb je wel een 3D bril nodig.",Key = "lm_3d", Name="3D", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=15},
            new SpecificationDefinition {Description = "De afstand tussen twee beeldpunten (ook wel pixels genoemd), gemeten in millimeter. Hoe kleiner dit getal, hoe dichter de pixels bij elkaar staan en hoe scherper het beeld zal zijn. Gemiddelde waarden variëren tussen de 0,25 en 0,22.",Key = "pixel_pitch", Name="Pixel pitch", Type="text",Unit="text", Value="", ProductGroupID=15},
            new SpecificationDefinition {Description = "De helderheid van een lcd-monitor wordt uitgedrukt in candela per vierkante meter, de eenheid voor lichtsterkte. Hoe hoger dit getal, hoe natuurlijker het beeld overkomt. Gemiddelde waarden variëren tussen de 300 en 500.",Key = "brightness", Name="Helderheid", Type="number",Unit="number", Value="", ProductGroupID=15},
            new SpecificationDefinition {Description = "De contrast ratio is de verhouding tussen het aantal stappen die mogelijk zijn tussen zwart en wit. Een waarde van 1000:1 geeft bijvoorbeeld aan dat de monitor het verloop van zwart naar wit in duizend stappen kan weergeven. Een hogere waarde betekent dus een beter contrast. Gemiddelde waarden variëren tussen de 300 en 1000.",Key = "lm_contrast_n", Name="Contrast ratio", Type="number",Unit="number", Value="", ProductGroupID=15},
            new SpecificationDefinition {Description = "Het dynamisch contrast geeft het aantal stappen aan tussen zwart en wit onder de meest gunstige omstandigheden. Onder donkere omstandigheden brandt de backlight van de lcd-monitor minder fel en bij heldere beelden juist feller. Hierdoor wordt het aantal stappen tussen zwart en wit veel groter, waardoor het dynamisch contrast altijd een veel hogere waarde heeft dan het normale schermcontrast.",Key = "lm_dynamischcontrast", Name="Dynamisch contrast", Type="number",Unit="number", Value="", ProductGroupID=15},
            new SpecificationDefinition {Description = "De tijd die een lcd-cel nodig heeft om van actief (zwart) naar inactief (wit) en weer terug naar zwart te gaan. Hoe lager dit getal, hoe sneller de lcd-monitor reageert. Vooral bij het spelen van games en het bekijken van films is een lage reactiesnelheid aan te raden. Is die te hoog, dan kan er bij snelle beelden een waas ontstaan. Gemiddelde waarden variëren tussen de 8 en 2.",Key = "lm_resptime", Name="Reactiesnelheid", Type="number",Unit="number", Value="", ProductGroupID=15},
            new SpecificationDefinition {Description = "Een lcd-monitor heeft eigenlijk maar één optimale resolutie en die wordt hier gegeven. Houd er rekening mee dat je videokaart of grafische chip deze resolutie ook moet ondersteunen.",Key = "monres_nw", Name="Resolutie (max.)", Type="dialog",Unit="dialog", Value="", ProductGroupID=15},
            new SpecificationDefinition {Description = "Met een touchscreen is het gebruik van een muis bijna overbodig, je raakt de pictogrammen aan op het beeldscherm.",Key = "touch", Name="Touchscreen", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=15},
            new SpecificationDefinition {Description = "De maximale hoek waaronder je van links of rechts naar de lcd-monitor kunt kijken, zonder dat het beeld niet meer (goed) te zien is.",Key = "viewangleh", Name="Horizontale kijkhoek (max.)", Type="number",Unit="number", Value="", ProductGroupID=15},
            new SpecificationDefinition {Description = "De maximale hoek waaronder je van boven of onder naar de lcd-monitor kunt kijken, zonder dat het beeld niet meer (goed) te zien is.",Key = "viewanglev", Name="Verticale kijkhoek (max.)", Type="number",Unit="number", Value="", ProductGroupID=15},
            new SpecificationDefinition {Description = "Deze getallen geven aan, in kilohertz, hoe snel een enkele horizontale lijn op het scherm wordt getekend. Hogere snelheden zorgen voor een duidelijker en stabieler beeld op hogere resoluties.",Key = "hfrequency", Name="Horizontaal", Type="text",Unit="text", Value="", ProductGroupID=15},
            new SpecificationDefinition {Description = "Deze getallen geven aan, in kilohertz, hoe snel een enkele verticale lijn op het scherm wordt getekend. Hogere snelheden zorgen voor een duidelijker en stabieler beeld op hogere resoluties. Deze waarde staat ook wel bekend als refresh rate of verversingsfrequentie.",Key = "vfrequency", Name="Verticaal", Type="text",Unit="text", Value="", ProductGroupID=15},
