            new SpecificationDefinition {Description = "Het type aansluiting waarover de lcd-monitor beschikt. Dit kunnen er verschillende zijn, zoals DVI, D-Sub of HDMI. Bovendien bestaan er verloopstukjes voor bijvoorbeeld DVI naar D-sub.",Key = "interface", Name="Aansluitingen", Type="dialog",Unit="dialog", Value="", ProductGroupID=15},
            new SpecificationDefinition {Description = "HDCP staat voor 'High-Bandwidth Digital Content Protection'. HDCP is een bescherming van digitale audio- en videosignalen die worden verstuurd via een DVI- of HDMI-verbinding. Deze techniek moet het illegaal kopiëren van bijvoorbeeld films en muziek tegengaan. Bepaalde apparaten, zoals Bluray-spelers, vereisen een afspeelapparaat (monitor of tv) met HDCP, anders wordt de inhoud in een lagere resolutie (oftewel slechtere beeldkwaliteit) of zelfs helemaal niet weergegeven.",Key = "hdcp", Name="HDCP", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=15},
            new SpecificationDefinition {Description = "Een lcd-monitor kan beschikken over ingebouwde luidsprekers. Je kunt hier echter beter niet te veel van verwachten, want het geproduceerde geluid is niet te vergelijken met dat van een losse speakerset.",Key = "speakers_yn", Name="Speakers ingebouwd", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=15},
            new SpecificationDefinition {Description = "Als de lcd-monitor over ingebouwde luidsprekers beschikt, lees je hier het muziekvermogen.",Key = "lm_vermogenspeakers", Name="Vermogen speakers", Type="number",Unit="number", Value="", ProductGroupID=15},
