            new SpecificationDefinition {Description = "Hoe meer megapixels hoe groter je de foto kunt uitprinten zonder kwaliteitsverlies. Voor afdrukken op A4 formaat kies je minimaal voor 9 megapixels.",Key = "pixelnum", Name="Resolutie", Type="number",Unit="number", Value="", ProductGroupID=7},
            new SpecificationDefinition {Description = "Een full frame sensor is 24x36 mm en heeft dezelfde maat als een 35mm kleinbeeld negatief. Deze sensoren komen vooral voor in professionele modellen. Met een full frame bereik je maximale scherpte, detail en dynamisch contrast. Vooral professionele fotografen werken met full frame camera's.",Key = "gen_full_frame_sensor", Name="Full frame sensor", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=7},
            new SpecificationDefinition {Description = "",Key = "slr_beeldsensortype", Name="Type beeldsensor", Type="dialog",Unit="dialog", Value="", ProductGroupID=7},
            new SpecificationDefinition {Description = "De kwaliteit van een digitale camera wordt voor een belangrijk deel bepaald door de grootte van de beeldsensor. In het algemeen geldt, hoe groter de sensor, hoe beter de kwaliteit van de foto's (scherpte, lichtgevoeligheid, hoog dynamisch contrast en minder ruis).",Key = "slr_beeldsensor", Name="Formaat beeldsensor", Type="text",Unit="text", Value="", ProductGroupID=7},
