            new SpecificationDefinition {Description = "Hoe hoger de resolutie, hoe groter je de foto kunt afdrukken zonder kwaliteitsverlies. Voor afdrukken van A4-formaat kies je voor minimaal zo'n 3400 x 2600 pixels.",Key = "resmax", Name="Maximale fotoresolutie", Type="text",Unit="text", Value="", ProductGroupID=7},
