            new SpecificationDefinition {Description = "Als je met een digitale SLR kunt filmen in Full HD wil dat zeggen dat je filmt in een resolutie van 1920 x 1080. Dit geeft een zeer hoge beeldkwaliteit, zeker wanneer deze beelden bekeken worden op een daarvoor geschikte Full HD televisie.",Key = "filmeninfullhd", Name="Filmen in Full HD", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=7},
