            new SpecificationDefinition {Description = "Met een autofocus hulplicht kan de camera beter scherpstellen in lastige situaties. Bijvoorbeeld in het donker, of bij onderwerpen met een egale kleur of weinig contrast.",Key = "af_hulplicht", Name="AF hulplicht", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=7},
            new SpecificationDefinition {Description = "",Key = "lach_detectie", Name="Lach detectie", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=7},
            new SpecificationDefinition {Description = "Camera's hebben meestal meerdere scherpstelmethodes. Een handig optie voor wanneer je bewegende voorwerpen continue scherp wilt houden is de optie  <b>AI Servo</b> ook bekend als <b>Servo</B> of <b>autofocus continu</b>.",Key = "slr_scherpstelmethodes", Name="Scherpstelling methodes", Type="dialog",Unit="dialog", Value="", ProductGroupID=7},
            new SpecificationDefinition {Description = "De zelfontspanner is een functie op je camera waarmee foto's genomen kunnen worden met een vertraging tussen het indrukken van de ontspanknop en het maken van de foto. Met een zelfontspanner kun je dus ook zélf een keer op de foto.",Key = "slr_zelfontspanner", Name="Zelfontspanner", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=7},
            new SpecificationDefinition {Description = "Met deze instelling op je camera kun je snel een aantal foto's achter elkaar maken. Dit is vooral handig bij bewegende beelden, bijvoorbeeld van spelende kinderen of bij een sportwedstrijd. Vervolgens kun je de beste foto kiezen.",Key = "serieopname", Name="Serieopname", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=7},
            new SpecificationDefinition {Description = "",Key = "slr_inclusief_objectief", Name="Inclusief objectief", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=7},
            new SpecificationDefinition {Description = "De beeldstabilisator van je camera helpt bij het opvangen van schokken en trillingen. Vooral bij lange sluitertijden of grote zoomafstanden zorgen de kleinste bewegingen al snel voor onscherpe foto's. De stabilisator corrigeert al die bewegingen, zodat je toch een strakke foto/video kunt maken.",Key = "slr_beeldstabilisator", Name="Beeldstabilisatie", Type="dialog",Unit="dialog", Value="", ProductGroupID=7},
            new SpecificationDefinition {Description = "Optisch zoomen is met behoud van beeldkwaliteit. Bij digitale zoom wordt er ingezoomd op een deel van de foto en lever je veel aan kwaliteit in. Daarom is een optisch zoomen beter. Voor gewoon gebruik is 3 tot 5x optische zoom prima.",Key = "zoomoptical", Name="Optische zoom", Type="number",Unit="number", Value="", ProductGroupID=7},
            new SpecificationDefinition {Description = "<b>Belangrijk: Bekijk goed welk type lensvatting jouw camera heeft als je een objectief gaat aanschaffen.</b> Zo ben je er zeker van dat het objectief op jouw camera past.",Key = "objectiefbajonet", Name="Objectiefbajonet", Type="dialog",Unit="dialog", Value="", ProductGroupID=7},
            new SpecificationDefinition {Description = "",Key = "slr_brandpunt_digitaal", Name="Brandpunt (digitaal)", Type="text",Unit="text", Value="", ProductGroupID=7},
            new SpecificationDefinition {Description = "Omdat de brandpuntsafstanden uit de analoge fotografie zo ingeburgerd zijn geeft men meestal ter vergelijking de overeenkomstige brandpuntsafstand van een ouderwetse 35-mm fotocamera.",Key = "slr_lens", Name="Brandpunt (35mm equivalent)", Type="text",Unit="text", Value="", ProductGroupID=7},
            new SpecificationDefinition {Description = "",Key = "slr_diafragmawaardes", Name="Diafragmawaardes f/", Type="text",Unit="text", Value="", ProductGroupID=7},
