            new SpecificationDefinition {Description = "Bepaal zelf welk onderdeel van de belichting de voorkeur heeft. Zet je diafragma of sluitertijd vast. De camera regelt dan zelf de juiste belichting aan de hand van jouw voorkeur!",Key = "slr_belichtingsprogrammas", Name="Belichtingsprogramma's", Type="dialog",Unit="dialog", Value="", ProductGroupID=7},
