            new SpecificationDefinition {Description = "Hoe lager de ISO waarde hoe minder lichtgevoelig. Je moet dus fotograferen met langere sluitertijden. Een lage ISO betekent echter wel zeer weinig tot geen beeldruis. Een lage ISO waarde is dus bijzonder geschikt om stillevens of portretten in hoge kwaliteit vast te leggen. Het beste kun je dan wel vanaf een statief werken.",Key = "isorating", Name="ISO-waarden", Type="text",Unit="text", Value="", ProductGroupID=7},
            new SpecificationDefinition {Description = "",Key = "laagste_iso", Name="Laagste ISO-waarde", Type="number",Unit="number", Value="", ProductGroupID=7},
            new SpecificationDefinition {Description = "Een hogere ISO waarde betekent een grotere lichtgevoeligheid. Hiermee kun je fotograferen met korte sluitertijden en zodoende snelle bewegingen vastleggen. Ook kun je in donkere omstandigheden nog langer fotograferen zonder gebruik van een flitser. Een hoge ISO heeft echter wel een nadeel. Hoe hoger de ISO waarde hoe meer ruis in de foto.",Key = "hoogste_iso", Name="Hoogste ISO-waarde", Type="number",Unit="number", Value="", ProductGroupID=7},
            new SpecificationDefinition {Description = "De camera kan op verschillende manieren het licht meten. Een handige optie is \"spotmeting\". Hiermee kun je in moeilijke lichtomstandigheden vaak toch de juiste belichting bepalen.",Key = "lichtmeting", Name="Lichtmeting", Type="dialog",Unit="dialog", Value="", ProductGroupID=7},
            new SpecificationDefinition {Description = "Met deze functie kun je de door de camera berekende belichting bewust over- of onderbelichten. Dit is vooral handig in moeilijke lichtomstandigheden zoals bijvoorbeeld een zwarte kat in de sneeuw.",Key = "slr_belichtingscorrectie", Name="Belichtingscorrectie", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=7},
