            new SpecificationDefinition {Description = "Een histogram is een grafiek die de verdeling tussen de lichte en donkere delen op de foto weergeeft. Het is een ideaal hulpmiddel om in één oogopslag te zien of de foto goed belicht is.",Key = "histogram", Name="Histogram", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=7},
