            new SpecificationDefinition {Description = "Het meest gebruikte formaat is JPEG. Bij dit formaat wordt het fotobestand verkleind met minimaal kwaliteit/gegevensverlies. Voor maximale kwaliteit en bewerkingsmogelijkheden kies je voor RAW. Dit formaat wordt vooral gebruikt door professionele fotografen.",Key = "bestandsformaten", Name="Bestandsformaten", Type="dialog",Unit="dialog", Value="", ProductGroupID=7},
            new SpecificationDefinition {Description = "Bij camera's met deze functie kun je foto's rechtstreeks van de camera printen op een speciale fotoprinter, zonder ze eerst op een computer te zetten. De officiële naam voor dit systeem is PictBridge, maar sommige fabrikanten geven het een eigen naam zoals: DirectPrint en DPOF.",Key = "slr_dc_pictbridge", Name="PictBridge", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=7},
