            new SpecificationDefinition {Description = "Dit getal geeft aan hoeveel meters de flitser maximaal kan halen bij een foto met flits. Het richtgetal dat bij de specificaties van de camera  wordt aangegeven is voor fotograferen met de lens op de groothoekstand. Fotografeer je op tele-stand dan is het richtgetal ongeveer 30% minder.",Key = "richtgetal", Name="Richtgetal", Type="number",Unit="number", Value="", ProductGroupID=7},
            new SpecificationDefinition {Description = "Naast de volautomatische flits beschikken SLR camera's over instellingen als \"tweede gordijn\" of \"slow-syc\". Zeker slow sync is interessant om zowel het hoofdonderwerp als de achtergrond sfeervol uitgelicht te krijgen.<b>Tip:</b> werk met deze stand vanaf statief.",Key = "flitsprogramma", Name="Flitsprogramma's", Type="dialog",Unit="dialog", Value="", ProductGroupID=7},
