            new SpecificationDefinition {Description = "NFC (Near Field Communication) is een draadloze communicatiemethode voor de korte afstand (max. 10 cm) en bedoeld om data tussen apparaten onderling uit te wisselen door ze bijvoorbeeld even tegen elkaar aan te houden.",Key = "gen_nfc", Name="Near Field Communication (NFC)", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=7},
            new SpecificationDefinition {Description = "Naast USB en AV beschikken steeds meer camera’s over een HDMI if WiFi aansluiting. Met WiFi kun je de beelden draadloos naar je pc of laptop sturen. Tevens is het mogelijk om met WiFi de beelden rechtstreeks te uploaden naar sociale netwerken als facebook of youtube.",Key = "interface", Name="Aansluitingen", Type="dialog",Unit="dialog", Value="", ProductGroupID=7},
            new SpecificationDefinition {Description = "Deze functie zorgt ervoor dat, elke keer dat je de camera aan- of uitzet, het CCD of CMOS element van stofjes wordt ontdaan, bijvoorbeeld door het elementje even te laten trillen. Zo voorkom / verminder je stofvorming op de sensor.",Key = "slr_antistof-functie", Name="Antistof-functie", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=7},
