            new SpecificationDefinition {Description = "Een seniorentelefoon is een telefoon met extra opties voor ouderen. Deze toestellen bevatten extra grote knoppen, zijn te gebruiken met gehoorapparaten, hebben vaak een makkelijk te vinden sos-knop en zijn eenvoudig in gebruik.",Key = "seniorentelefoon", Name="Seniorentelefoon", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=22},
            new SpecificationDefinition {Description = "Als de telefoon is voorzien van meer dan 1 handset, kun je bijvoorbeeld op de slaapkamer ook een handset neerzetten. Zo ben je altijd bereikbaar of kun je snel bellen in geval van nood. Een veilig gevoel!",Key = "handset_count", Name="Aantal handsets", Type="number",Unit="number", Value="", ProductGroupID=22},
            new SpecificationDefinition {Description = "Vaak kun je meerdere handsets op het basisstation aansluiten. Je kunt dan intern bellen van de ene naar de andere handset. Hier wordt aangegeven hoeveel handsets maximaal aangesloten kunnen worden op het basisstation.",Key = "handsetmax", Name="Aantal handsets (max.)", Type="number",Unit="number", Value="", ProductGroupID=22},
            new SpecificationDefinition {Description = "Als het toestel beschikt over een antwoordapparaat kunnen er digitaal een aantal berichten opgenomen worden op het basisstation.",Key = "answering", Name="Antwoordapparaat", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=22},
            new SpecificationDefinition {Description = "Een display maakt het mogelijk om naast het gedraaide nummer bijvoorbeeld ook de menustructuur duidelijk weer te geven.",Key = "lcddisplay", Name="Display", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=22},
