            new SpecificationDefinition {Description = "De beltoon hype is onder de mobiele telefoons in volle gang, maar de huistelefoon kan niet achter blijven. Polyfone beltonen hebben meerdere geluidskanalen beschikbaar, waardoor deze beltonen meer klinken als echte muziek. De monofone beltonen klinken eenvoudiger.",Key = "beltonen", Name="Beltonen", Type="dialog",Unit="dialog", Value="", ProductGroupID=22},
            new SpecificationDefinition {Description = "Met een telefoonboek kun je veelgebruikte nummers opslaan op de handset. Wanneer er meerdere handsets zijn aangemeld op 1 basisstation is het vaak mogelijk een nummer in één keer op te slaan in alle aangemelde handsets tegelijk.",Key = "numbermemory", Name="Telefoonboek", Type="number",Unit="number", Value="", ProductGroupID=22},
