            new SpecificationDefinition {Description = "De redialfunctie is eigenlijk een lijst met de laatst gedraaide nummers, die in het toestel wordt opgeslagen. Je hoeft dan niet telkens het nummer op te zoeken. Voor de meeste mensen zijn 10 nummers meer dan voldoende. Deze lijst bestaat naast het telefoonboek met namen en nummers.",Key = "redialmemory", Name="Redial geheugen", Type="number",Unit="number", Value="", ProductGroupID=22},
            new SpecificationDefinition {Description = "Als het toestel voorzien is van een speakerfunctie, kun je andere mensen mee laten luisteren met een gesprek. Via de handsfree speaker is het mogelijk om handsfree te bellen. Wel kan het dan zijn dat de persoon met wie je belt, je niet meer goed kan horen (vanwege de afstand tussen je mond en de microfoon).",Key = "speaker", Name="Speakerfunctie", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=22},
            new SpecificationDefinition {Description = "Deze functie geeft de gespreksduur weer op het display van de telefoon.",Key = "calltimer", Name="Call-timer", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=22},
            new SpecificationDefinition {Description = "Deze functie geeft aan of er een voicemailbericht voor je is. Niet te verwarren met een antwoordapparaat. Voicemail is een service van de telefoonaanbieder. Een eventueel bericht staat bij hun opgeslagen en er moet (eventueel tegen betaling) gebeld worden om deze te beluisteren.",Key = "voicemail", Name="Voicemail indicatie", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=22},
            new SpecificationDefinition {Description = "Als het toestel beschikt over een intercom functie kun je bellen tussen de handsets en het basisstation. Je kunt dus intern gesprekken voeren.",Key = "intercom", Name="Intercom functie", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=22},
            new SpecificationDefinition {Description = "Sommige toestellen beschikken over een babyfoonfunctie. Zodra de handset geluid bij de baby waarneemt, belt deze naar, afhankelijk van de instelling, een tweede handset of een voorgeprogrammeerd extern telefoonnummer.",Key = "babyfoon", Name="Babyfoon", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=22},
            new SpecificationDefinition {Description = "Als een telefoon intern kunt doorverbinden, kun je bijvoorbeeld op toestel 1 de telefoon opnemen en het gesprek doorverbinden naar toestel 2, bijvoorbeeld naar iemand die in een andere kamer.",Key = "interndoorv", Name="Intern doorverbinden", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=22},
            new SpecificationDefinition {Description = "",Key = "extra", Name="Extra functies", Type="dialog",Unit="dialog", Value="", ProductGroupID=22},
