            new SpecificationDefinition {Description = "",Key = "def_truecolors", Name="Kleuren", Type="dialog",Unit="dialog", Value="", ProductGroupID=22},
            new SpecificationDefinition {Description = "",Key = "def_truecolors", Name="Kleuren", Type="dialog",Unit="dialog", Value="", ProductGroupID=16},
            new SpecificationDefinition {Description = "Voor draadloos internet zijn er vier standaarden ontwikkeld. De meest recente standaard voor draadloos internet is 802.11n, met een theoretische doorvoersnelheid van 300 Mb/s (megabit per seconde). Ook 802.11g wordt nog volop gebruikt, deze standaard heeft een theoretische doorvoersnelheid van 54 Mb/s. 802.11a en 802.11b zijn inmiddels verouderd en worden vrijwel niet meer gebruikt.",Key = "wl_standard", Name="Ondersteunde standaarden", Type="dialog",Unit="dialog", Value="", ProductGroupID=16},
            new SpecificationDefinition {Description = "Dit is de maximale theoretische datasnelheid die een WiFi-apparaat kan halen. Deze door de fabrikant opgegeven waarde is meestal niet erg reëel, omdat er een hoop zogehete 'overhead’ bestaat, waardoor de echte snelheid lager komt te liggen. Ook zijn er grote verschillen tussen merken en apparaten onderling. Lees dus, indien dit voor u belangrijk is, goed de reviews van andere gebruikers door om tot een zo goed mogelijk beeld van de ‘echte’ snelheid te komen. In de regel zal dit tot zo’n 60% van de maximale snelheid zijn.",Key = "wl_speed_wa", Name="Ondersteunde max. snelheid", Type="number",Unit="number", Value="", ProductGroupID=16},
            new SpecificationDefinition {Description = "Het frequentiebereik is de frequentie waarop het apparaat zijn radiogolven uitzendt. Een normale WiFi-verbinding tussen twee apparaten heeft één kanaal nodig om gegevens heen en weer te kunnen versturen.",Key = "wl_freqrange", Name="Frequentiebereik", Type="text",Unit="text", Value="", ProductGroupID=16},
            new SpecificationDefinition {Description = "In principe geldt: hoe meer antennes, hoe beter het draadloze signaal kan zijn.",Key = "wl_antnum", Name="Aantal antennes", Type="number",Unit="number", Value="", ProductGroupID=16},
            new SpecificationDefinition {Description = "Als een router afneembare antennes heeft, kun je deze zelf vervangen door andere (betere) antennes. Dit kan handig zijn als je op bepaalde plekken in huis een slecht bereik hebt en dat wilt verbeteren.",Key = "wl_antrem", Name="Afneembare antenne(s)", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=16},
            new SpecificationDefinition {Description = "WEP staat voor Wired Equivalent Privacy. Deze enigszins verouderde beveiligingsoptie zorgt voor de versleuteling van de informatie die u verstuurt over uw draadloze netwerk, zodat niemand deze kan lezen zonder de sleutel te hebben. Hoe meer bits de WEP-encryptie heeft, hoe langer het voor een hacker duurt om de verbinding te kraken.",Key = "wl_wep", Name="WEP-encryptie", Type="dialog",Unit="dialog", Value="", ProductGroupID=16},
            new SpecificationDefinition {Description = "WPA staat voor WiFi Protected Access, en is een betere beveiligingsstandaard dan WEP. Dit komt omdat niet alleen simpele letters en cijfers voor het wachtwoord gebruikt kunnen worden, maar ook leestekens en andere symbolen.",Key = "wl_wpa", Name="WPA", Type="dialog",Unit="dialog", Value="", ProductGroupID=16},
            new SpecificationDefinition {Description = "Elke draadloos apparaat heeft een uniek nummer, welke het MAC (Media Access Control)-adres wordt genoemd. Een simpel voorbeeld van een fictief MAC-adres is: 12-34-56-78-9A-BC. Het filteren hierop betekent dat alleen netwerkadapters met MAC-adressen die u vantevoren instelt, op uw netwerk kunnen komen. Dit is veruit de eenvoudigste en vaak ook beste beveiliging tegen indringers op uw netwerk.",Key = "wl_macfilt", Name="Filtering op MAC-adres", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=16},
            new SpecificationDefinition {Description = "Dynamic DNS (of Dyn-DNS) staat voor Dynamic Domain Name Server. Deze service zorgt ervoor dat wanneer uw IP-adres verandert, uw pc nog steeds onder dezelfde naam bereikbaar is. Dit kan op twee manieren: binnen uw eigen netwerk of op het internet.",Key = "wl_dyndns", Name="Dynamic DNS", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=16},
            new SpecificationDefinition {Description = "DHCP staat voor Dynamic Host Configuration Protocol. Deze service zorgt voor een automatische uitdeling van IP-adressen aan alle apparaten op het (draadloze) netwerk, zodat u niet handmatig een IP-adres hoeft in te stellen op uw PC.",Key = "wl_dhcp", Name="DHCP-server", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=16},
            new SpecificationDefinition {Description = "",Key = "wl_statrout", Name="Statische routering", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=16},
            new SpecificationDefinition {Description = "Een firewall zorgt ervoor dat er geen verdachte data van jouw pc naar buiten wordt gestuurd.",Key = "wl_firewall", Name="Firewall", Type="dialog",Unit="dialog", Value="", ProductGroupID=16},
            new SpecificationDefinition {Description = "Met deze service kunt u een blokkering van bepaalde websites instellen. Gebruikers van uw netwerk (bijvoorbeeld uw kinderen) kunnen deze sites dan niet bezoeken.",Key = "wl_urlfilt", Name="URL-filtering", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=16},
            new SpecificationDefinition {Description = "De service virtuele servers kunt u gebruiken indien u zelf een website wilt hosten vanaf uw eigen computer, of bijvoorbeeld een netwerk-camera direct vanaf internet wilt kunnen bekijken. Met de Virtuele Server-instelling weet de router of modem dat het verkeer dat naar u toe komt, doorgestuurd mag worden naar de pc of server waar uw website op staat, of bijvoorbeeld naar uw netwerk-camera.",Key = "wl_virtual", Name="Ondersteuning virtuele servers", Type="yes_no",Unit="yes_no", Value="", ProductGroupID=16},
